then.script:
var payload = mc.getPayloadXML();
delete payload.RequestedElements;
mc.setPayloadXML(payload);

				    var payload = mc.getPayloadXML();
                    delete payload.ProcessingConditions;
					mc.setPayloadXML(payload);

else.script:
var payload = mc.getPayloadXML();
var queryHitsUnlimitedIndicator = payload.ProcessingConditions.QueryHitsUnlimitedIndicator;
if(queryHitsUnlimitedIndicator == "" || queryHitsUnlimitedIndicator == null) {
    delete payload.ProcessingConditions;
    mc.setPayloadXML(payload);
}

sequence.script:
var payload = mc.getPayloadXML();
var objs = payload.child("SalesOrderSelectionByElements").children();
for (var x=0; x < objs.length(); x++) {
    var localName = objs[x].localName();
    var indexOfIBTC = objs[x].toString().indexOf("<IntervalBoundaryTypeCode>");
    if ((localName == "SelectionByID" || localName == "SelectionByBuyerPartyID"
    || localName == "SelectionByItemListCustomerOrderLifeCycleStatusCode"
    || localName == "SelectionByName" || localName == "SelectionByBuyerID"
    || localName == "SelectionByPostingDate" || localName == "SelectionByDataOriginTypeCode"
    || localName == "SelectionByItemListExecutionReleaseStatusCode" || localName == "SelectionByApprovalStatusCode"
    || localName == "SelectionByEmployeeResponsiblePartyID" || localName == "SelectionBySalesUnitPartyID"
    || localName == "SelectionBySalesAndServiceBusinessAreaSalesOrganisationID"
    || localName == "SelectionBySalesAndServiceBusinessAreaDistributionChannelCode"
    || localName == "SelectionByServicePerformerPartyID" || localName == "SelectionByServiceExecutionTeamPartyID"
    || localName == "SelectionByProductID" || localName == "SelectionByProductRequirementSpecificationID"
    || localName == "SelectionByLastChangedDate")
    && indexOfIBTC == -1) {
        delete objs[x];
        mc.setPayloadXML(payload);
    } else if (indexOfIBTC > -1 && (objs[x].child("IntervalBoundaryTypeCode") == ""
    || objs[x].child("IntervalBoundaryTypeCode") == null)) {
        delete objs[x];
        mc.setPayloadXML(payload);
    }
}
